local enemies={}

function draw_player(p)
	local ix,iy=calc_iso(p.x,p.y)
	spr_iso(p.sprite,
		ix,iy,
		0,1,p.facing,0,2,3,1)
end

function create_enemy(x,y)
	local enemy = {
		x=x,y=y,sprite=305
	}

	function enemy:draw()
		local ix,iy=calc_iso(self.x,self.y)
		spr_iso(self.sprite,
			ix,iy,0,1,0,0,2,3,1)
	end

	table.insert(enemies,enemy)
	return enemy
end

